import React from 'react';

export class Button extends React.Component {
    handleClick = () => {
        var val = parseInt(this.props.children);
        alert(val);
    }

    render() {
        return (
            <div className="my-2 px-2 w-1/3 overflow-hidden sm:my-2 sm:px-2 sm:w-1/3 md:my-2 md:px-2 md:w-1/3 lg:my-2 lg:px-2 lg:w-1/3 xl:my-2 xl:px-2 xl:w-1/3">
                <button value={this.props.children} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded" onClick={this.handleClick}>{this.props.children}</button>
            </div>
        );
    }
}
